@jtu.with_config(jax_numpy_dtype_promotion="standard")
class Autotune(jtu.JaxTestCase):

  @parameterized.product(
      page_size=[256],
      # TODO(kyuyeunk, jevinjiang): autotune for quantized type.
      q_dtype=[jnp.bfloat16],
      kv_dtype=[jnp.float8_e4m3fn],
      num_q_heads=[32],
      num_kv_heads=[4],
      head_dim=[128,],
      max_model_len=[5120],
      max_num_tokens=[256],
      max_num_seqs=[256],
      bkv_p_lst=[(1, 2, 4, 8, 16, 32, 64, 128)],
      bq_sz_lst=[(1,)],
      bq_csz_lst=[(1,)],
      bkv_cp_lst=[(1, 2, 4, 8, 16, 32, 64, 128)],
  )
  def test_autotune(
      self,
      page_size,
      q_dtype,
      kv_dtype,
      num_q_heads,
      num_kv_heads,
      head_dim,
      max_model_len,
      max_num_tokens,
      max_num_seqs,
      bkv_p_lst,
      bq_sz_lst,
      bq_csz_lst,
      bkv_cp_lst,
  ):
    # Currently we only use one example to autotune. If necessary, we can
    # construct decode-heavy or prefill-heavy examples.
    example = get_qkv_lens_example(
        max_num_tokens,
        max_model_len,
        actual_num_seqs=max_num_seqs,
    )
    if num_q_heads % num_kv_heads != 0:
      return
    if max_model_len < page_size:
      return
    print(f"[Debug] {example=}")

    rows = []
    key = get_simplified_raw_key(
        page_size,
        q_dtype,
        kv_dtype,
        num_q_heads,
        num_kv_heads,
        head_dim,
        max_model_len,
    )
    # best_block_size: (num_kv_pages_per_block, num_q_per_block).
    best_block_size = autotune(
        example,
        key,
        max_num_tokens,
        max_num_seqs,
        bkv_p_lst,
        bq_sz_lst,
        bq_csz_lst,
        bkv_cp_lst,
        num_iterations=100,
    )
    if best_block_size is not None:
      rows.append(f"{key}: {best_block_size},")

    print(rows)


